    &lt;pathelement path=&quot;${dir.base}&quot;/&gt;
    &lt;pathelement path=&quot;${dir.dist}/${file.jar}&quot;/&gt;
  &lt;/path&gt;

  &lt;target name=&quot;compile-tests&quot; description=&quot;Test with JUnit&quot; 
depends=&quot;compile&quot;&gt;
  &lt;mkdir dir=&quot;build/test&quot;/&gt;
