      &lt;classpath refid=&quot;classpath&quot;/&gt;
      &lt;include name=&quot;**/*.java&quot;/&gt;
  &lt;/javac&gt;
  &lt;/target&gt;

  &lt;target name=&quot;run-tests&quot; description=&quot;Test with Junit&quot; 
depends=&quot;compile-tests&quot;&gt;
