&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot;?&gt;
&lt;project basedir=&quot;.&quot; default=&quot;all&quot; name=&quot;servlets&quot;&gt;
    &lt;!-- Written to assume that classpath is rooted in the current
directory. --&gt;
    &lt;!-- So this should be OK if you make this script in the root of a
filesystem. --&gt;
    &lt;!-- If not, just change src.dir to be the root of your sources&#39;
package tree --&gt;
    &lt;!-- and use e.g. View over a Filesystem to mount that
subdirectory with all capabilities. --&gt;
    &lt;!-- The idea is that both Ant and NetBeans have to know what the
package root is --&gt;
    &lt;!-- for the classes in your application. --&gt;

    &lt;!-- Don&#39;t worry if you don&#39;t know the Ant syntax completely or
need help on some tasks! --&gt;
    &lt;!-- The standard Ant documentation can be downloaded from
AutoUpdate and --&gt;
    &lt;!-- and then you can access the Ant manual in the online help.
--&gt;

    &lt;taskdef resource=&quot;cactus.tasks&quot;&gt;
        &lt;classpath&gt;
            &lt;pathelement location=&quot;lib/cactus-1.5.jar&quot;/&gt;
            &lt;pathelement location=&quot;lib/cactus-ant-1.5.jar&quot;/&gt;
            &lt;pathelement location=&quot;lib/commons-htt
pclient-2.0-rc2.jar&quot;/&gt;
            &lt;pathelement location=&quot;lib/commons-logging-1.0.3.jar&quot;/&gt;
            &lt;pathelement location=&quot;lib/aspectjrt-1.1.1.jar&quot;/&gt;
        &lt;/classpath&gt;
    &lt;/taskdef&gt;

    &lt;target name=&quot;init&quot;&gt;
        &lt;property location=&quot;classes&quot; name=&quot;classes.dir&quot;/&gt;
        &lt;property location=&quot;src&quot; name=&quot;src.dir&quot;/&gt;
        &lt;property location=&quot;/usr/local/tomcat50&quot; name=&quot;tomcat.dir&quot;/&gt;
        &lt;property location=&quot;${tomcat.dir}/bin&quot; name=&quot;tomcatbin.dir&quot;/&gt;
        &lt;property
location=&quot;/usr/local/tomcat50/webapps/test/WEB-INF/classes&quot;
name=&quot;deploy.dir&quot;/&gt;        
        &lt;property location=&quot;/usr/local/tomcat50/common/lib&quot;
name=&quot;libs.dir&quot;/&gt;
        &lt;property name=&quot;project.name&quot; value=&quot;${ant.project.name}&quot;/&gt;
        &lt;property location=&quot;webapp&quot; name=&quot;src.webapp.dir&quot;/&gt;
        &lt;property location=&quot;test-reports&quot; name=&quot;testreports.dir&quot;/&gt;   

        &lt;property location=&quot;jars&quot; name=&quot;jars.dir&quot;/&gt;
        &lt;mkdir dir=&quot;${jars.dir}&quot;/&gt;
    &lt;/target&gt;

    &lt;target depends=&quot;init&quot; name=&quot;compile&quot;&gt;
        &lt;!-- Both srcdir and destdir should be package roots. --&gt;
        &lt;mkdir dir=&quot;${classes.dir}&quot;/&gt;
        &lt;javac debug=&quot;true&quot; deprecation=&quot;true&quot;
destdir=&quot;${classes.dir}&quot; srcdir=&quot;${src.dir}&quot;&gt;
            &lt;!-- To add something to the classpath: --&gt;
            &lt;classpath&gt;
                &lt;fileset dir=&quot;${libs.dir}&quot;&gt;
                    &lt;include name=&quot;**/*.jar&quot;/&gt;
                &lt;/fileset&gt;
            &lt;/classpath&gt;
            &lt;!-- To exclude some files: --&gt;
            &lt;!-- &lt;exclude name=&quot;com/foo/SomeFile.java&quot;/&gt;&lt;exclude
name=&quot;com/foo/somepackage/&quot;/&gt; --&gt;
        &lt;/javac&gt;
    &lt;/target&gt;

    &lt;target depends=&quot;init,TestAtOnce&quot; description=&quot;Build everything.&quot;
name=&quot;all&quot;/&gt;

    &lt;target depends=&quot;init&quot; description=&quot;Clean all build products.&quot;
name=&quot;clean&quot;&gt;
        &lt;delete dir=&quot;${classes.dir}&quot;/&gt;
    &lt;/target&gt;

&lt;!--    &lt;target description=&quot;Start Tomcat&quot; name=&quot;start.tomcat50&quot;&gt;
            &lt;exec dir=&quot;${tomcatbin.dir}&quot;
executable=&quot;${tomcatbin.dir}/startup.sh&quot;/&gt;
    &lt;/target&gt;
    &lt;target description=&quot;Stop Tomcat&quot; name=&quot;stop.tomcat50&quot;&gt;    
        &lt;exec dir=&quot;${tomcatbin.dir}&quot;
executable=&quot;${tomcatbin.dir}/shutdown.sh&quot;&gt;
          &lt;arg line=&quot;&quot;/&gt;
        &lt;/exec&gt;
    &lt;/target&gt;
    --&gt;


    &lt;target name=&quot;TestAtOnce&quot; depends=&quot;compile&quot;&gt;
        &lt;jar destfile=&quot;${jars.dir}/myjars.jar&quot; bas
edir=&quot;${classes.dir}&quot;/&gt;
        &lt;war warfile=&quot;test.war&quot;
webxml=&quot;${src.webapp.dir}/WEB-INF/web.xml&quot;&gt;
            &lt;lib dir=&quot;${jars.dir}&quot;/&gt;
            &lt;lib dir=&quot;${libs.dir}&quot;/&gt;
        &lt;/war&gt;
        &lt;!-- Cactify the web-app archive --&gt;
        &lt;cactifywar srcfile=&quot;test.war&quot; destfile=&quot;test-cactified.war&quot;&gt;
            &lt;servletredirector/&gt;
            &lt;jspredirector/&gt;
        &lt;/cactifywar&gt;
        &lt;cactus warfile=&quot;test-cactified.war&quot; fork=&quot;yes&quot;
failureproperty=&quot;tests.failed&quot; haltonerror=&quot;true&quot;&gt;
            &lt;!-- The following addition of jars.dir (for myjars.jar)
to classpath seems redundant.
            But then only it works!! --&gt;
            &lt;classpath&gt;
                &lt;fileset dir=&quot;${jars.dir}&quot;&gt;
                    &lt;include name=&quot;**/*.jar&quot;/&gt;
                &lt;/fileset&gt;
            &lt;/classpath&gt;        
            &lt;classpath&gt;
                &lt;fileset dir=&quot;${libs.dir}&quot;&gt;
                    &lt;include name=&quot;**/*.jar&quot;/&gt;
                &lt;/fileset&gt;
            &lt;/classpath&gt;
            &lt;containerset&gt;
                &lt;tomcat5x dir=&quot;${tomcat.dir}&quot; /&gt;
            &lt;/containerset&gt;
            &lt;formatter type=&quot;plain&quot;/&gt;
            &lt;batchtest&gt;
                &lt;fileset dir=&quot;${src.dir}&quot;&gt;
                    &lt;include name=&quot;**/Test*.java&quot;/&gt;
                    &lt;exclude name=&quot;**/TestAll.java&quot;/&gt;
                &lt;/fileset&gt;
            &lt;/batchtest&gt;            
        &lt;/cactus&gt;
&lt;!-- Generate the JUnit reports --&gt;        
    &lt;junitreport todir=&quot;${testreports.dir}&quot;&gt;
      &lt;fileset dir=&quot;${testreports.dir}&quot;
          includes=&quot;TEST-*.xml&quot;/&gt;
      &lt;report todir=&quot;${testreports.dir}&quot;
          format=&quot;frames&quot;/&gt;
    &lt;/junitreport&gt;        
    &lt;/target&gt;    
&lt;/project&gt;
