Finally generate Html report for Test Suit Class through Ant.

build.xml

&lt;project name=&quot;Scandent JUnit Test&quot; basedir=&quot;.&quot; default=&quot;test&quot;&gt;
    &lt;!-- Local system paths --&gt;

      &lt;property name=&quot;target.classes&quot;           value=&quot;classes&quot;/&gt;
      &lt;property name=&quot;lib&quot;                value=&quot;lib&quot;/&gt;
    &lt;!-- classpath for required jar (JUnit,ant, etc.,) --&gt;
      &lt;path id=&quot;pocclasspath&quot;&gt;
            &lt;fileset dir=&quot;${lib}&quot;&gt;
                  &lt;patternset&gt;
                    &lt;include name=&quot;**/*.jar&quot;/&gt;
                  &lt;/patternset&gt;
             &lt;/fileset&gt;
             &lt;pathelement path =&quot;${target.classes}&quot;/&gt;
      &lt;/path&gt;

    &lt;!-- Make directories for JUnit Test Reports --&gt;
      &lt;target name=&quot;prepare&quot; &gt;
            &lt;mkdir dir=&quot;report/jUnit_report&quot;/&gt;
      &lt;/target&gt;
    &lt;!-- Remove classes directory for clean build --&gt;
      &lt;target name=&quot;clean&quot; description=&quot;Prepare for clean build&quot;&gt;
              &lt;delete dir=&quot;report&quot;/&gt;
      &lt;/target&gt;
    &lt;!-- Run the JUnit Test Case --&gt;
      &lt;target name=&quot;test1&quot; depends=&quot;clean,prepare&quot;&gt;
            &lt;junit printsummary=&quot;yes&quot; haltonerror=&quot;yes&quot;  fork=&quot;yes&quot;&gt;
                  &lt;formatter type=&quot;plain&quot; usefile=&quot;false&quot;/&gt;
                  &lt;formatter type=&quot;xml&quot;/&gt;
                  &lt;test name=&quot;com.workout.test.AllTests&quot;
todir=&quot;report/jUnit_report&quot;/&gt;
                  &lt;classpath refid=&quot;pocclasspath&quot;/&gt;
            &lt;/junit&gt;
      &lt;/target&gt;

    &lt;!-- Create report for JUnit results --&gt;
      &lt;target name=&quot;testreport&quot; depends=&quot;test1&quot;&gt;
            &lt;junitreport todir=&quot;report/jUnit_report&quot;&gt;
                  &lt;fileset dir=&quot;report/jUnit_report&quot;&gt;
                        &lt;include name=&quot;TEST*.xml&quot;/&gt;
                  &lt;/fileset&gt;
                  &lt;report format=&quot;frames&quot; todir=&quot;report/jUnit_report/html&quot;/&gt;
            &lt;/junitreport&gt;
      &lt;/target&gt;
      &lt;target name=&quot;test&quot; depends=&quot;testreport&quot;/&gt;

 &lt;/project&gt;


