The build file that is working for me is:

&lt;?xml version=&quot;1.0&quot;?&gt;
&lt;project name=&quot;JunitDemoWithTests2&quot; default=&quot;deploy&quot; &gt;
 	&lt;property name=&quot;HOME&quot; value=&quot;D:/JAVADevEnv/Source&quot; /&gt;
 	&lt;property name=&quot;SDKS_HOME&quot; value=&quot;D:/JAVADevEnv/SDKs&quot; /&gt;
 	&lt;property name=&quot;JAVA_HOME&quot; 
value=&quot;D:/JAVADevEnv/SDKs/j2sdk-1_4_2_04-windows-i586&quot; /&gt;
 	&lt;property name=&quot;CVSROOT&quot; value=&quot;D:/JAVADevEnv/CVS&quot; /&gt;
 	&lt;property name=&quot;TOOLS_HOME&quot; value=&quot;D:/JAVADevEnv/Tools&quot; /&gt;
 	&lt;property name=&quot;ANT_HOME&quot; 
value=&quot;D:/JAVADevEnv/Tools/apache-ant-1.6.2&quot; /&gt;
 	&lt;property name=&quot;JUNIT_HOME&quot; 
value=&quot;D:/JAVADevEnv/Tools/JUnit/junit3.8.1&quot; /&gt;
 	&lt;property name=&quot;CATALINA_HOME&quot; 
value=&quot;D:/JAVADevEnv/Tools/jakarta-tomcat-5.0.25&quot; /&gt;
 	&lt;property name=&quot;HTTPUNIT_HOME&quot; 
value=&quot;D:/JAVADevEnv/Tools/HTTPUnit/httpunit-1.6&quot; /&gt;
 	&lt;property name=&quot;MYSQL_HOME&quot; value=&quot;D:/JAVADevEnv/Tools/mysql&quot; /&gt;

   &lt;target name=&quot;init&quot;&gt;
 	&lt;mkdir dir=&quot;build/classes&quot; /&gt;
 	&lt;mkdir dir=&quot;dist&quot; /&gt;
     &lt;echo&gt;init complete!&lt;/echo&gt;
   &lt;/target&gt;

   &lt;target name=&quot;test-init&quot;&gt;
 	&lt;mkdir dir=&quot;test/classes&quot; /&gt;
     &lt;echo&gt;test-init complete!&lt;/echo&gt;
   &lt;/target&gt;

   &lt;target name=&quot;compile&quot; depends=&quot;init,test-init&quot;&gt;
     &lt;javac fork=&quot;yes&quot; srcdir=&quot;src&quot; destdir=&quot;build/classes&quot; &gt;
       &lt;classpath&gt;
   	    &lt;pathelement path=&quot;${JAVA_HOME}/bin&quot; /&gt;
   	    &lt;pathelement location=&quot;${JAVA_HOME}/bin/javac.exe&quot; /&gt;
   	    &lt;pathelement path=&quot;${JAVA_HOME}/lib&quot; /&gt;
       &lt;/classpath&gt;
     &lt;/javac&gt;
     &lt;echo&gt;compilation complete!&lt;/echo&gt;
   &lt;/target&gt;

   &lt;target name=&quot;test-compile&quot; depends=&quot;test-init,compile&quot;&gt;
     &lt;javac fork=&quot;yes&quot; srcdir=&quot;TestSrc&quot; includes=&quot;**/*.java&quot; 
destdir=&quot;test/classes&quot; &gt;
       &lt;classpath&gt;
   	    &lt;pathelement path=&quot;${JAVA_HOME}/bin&quot; /&gt;
   	    &lt;pathelement location=&quot;${JAVA_HOME}/bin/javac.exe&quot; /&gt;
   	    &lt;pathelement path=&quot;${JAVA_HOME}/lib&quot; /&gt;
 	    &lt;pathelement location=&quot;${JUNIT_HOME}/junit.jar&quot; /&gt;
         &lt;pathelement location=&quot;build/classes&quot; /&gt;
         &lt;pathelement location=&quot;test/classes&quot; /&gt;
       &lt;/classpath&gt;
     &lt;/javac&gt;
     &lt;echo&gt;JUnit Test-compile complete!&lt;/echo&gt;
   &lt;/target&gt;

   &lt;target name=&quot;unit-test&quot; depends=&quot;test-compile&quot;&gt;
     &lt;junit printsummary=&quot;true&quot; haltonfailure=&quot;true&quot; &gt;
       &lt;classpath&gt;
         &lt;pathelement path=&quot;build/classes&quot; /&gt;
         &lt;pathelement path=&quot;test/classes&quot; /&gt;
       &lt;/classpath&gt;
       &lt;batchtest fork=&quot;yes&quot; &gt;
         &lt;formatter type=&quot;plain&quot; usefile=&quot;false&quot; /&gt;
         &lt;fileset dir=&quot;test/classes&quot;&gt;
           &lt;include name=&quot;AllTests.class&quot; /&gt;
           &lt;include name=&quot;SimpleFractionTest.class&quot; /&gt;
         &lt;/fileset&gt;
       &lt;/batchtest&gt;
     &lt;/junit&gt;
     &lt;echo&gt;junit-run complete!&lt;/echo&gt;
   &lt;/target&gt;

   &lt;target name=&quot;deploy&quot; depends=&quot;compile,test-compile,unit-test,doc&quot;&gt;
     &lt;jar destfile=&quot;dist/project.jar&quot; basedir=&quot;build/classes&quot; /&gt;
     &lt;echo&gt;deploy complete!&lt;/echo&gt;
   &lt;/target&gt;
&lt;/project&gt;


