[The &quot;main&quot; method being tested.]

import java.io.*;
public class Main {
  public static void main(final String[] args) {
    if (args.length &gt; 0) {
      System.out.print(&quot;echo&quot;);
      for (int argIdx = 0; argIdx &lt; args.length; ++argIdx) {
        System.out.print(&quot; &quot; + args[argIdx]);
      }
      System.out.println();
    }
    final BufferedReader reader = new BufferedReader(new 
InputStreamReader(System.in));
    try {
      while (reader.ready()) {
        final String inputLine = reader.readLine();
        System.out.println(&quot;input: &quot; + inputLine);
      }
    } catch (final IOException ex) {
      ex.printStackTrace();
    }
  }
}

[The JUnit tests.]

import java.io.*;
import junit.framework.TestCase;
public class MainTest extends TestCase {
  public void testMain() {
    final String[] commandLineParameters = new String[] 
{&quot;A&quot;, &quot;B&quot;, &quot;C&quot;};
    final String systemIn =
      &quot;one&#92;n&quot; +
      &quot;two&#92;n&quot; +
      &quot;three&quot;;
    // Setup input and output.
    System.setIn(new ByteArrayInputStream(systemIn.getBytes()));
    final ByteArrayOutputStream output = new ByteArrayOutputStream();
    System.setOut(new PrintStream(output));
    // Call the main method
    Main.main(commandLineParameters);
    // Verify the output
    final String nl = &quot;&#92;r&#92;n&quot;;
    assertEquals(
      &quot;echo A B C&quot; + nl +
      &quot;input: one&quot; + nl +
      &quot;input: two&quot; + nl +
      &quot;input: three&quot; + nl,
      output.toString());
  }
  public void testMainNoData() {
    final String[] commandLineParameters = new String[] {};
    final String systemIn = &quot;&quot;;
    // Setup input and output.
    System.setIn(new ByteArrayInputStream(systemIn.getBytes()));
    final ByteArrayOutputStream output = new ByteArrayOutputStream();
    System.setOut(new PrintStream(output));
    // Call the main method
    Main.main(commandLineParameters);
    // Verify the output
    assertEquals(&quot;&quot;, output.toString());
  }
}
