Here&#39;s mine:

  &lt;!-- ==================================================================
--&gt;
  &lt;!-- Run all unit testcases
--&gt;
  &lt;!-- ==================================================================
--&gt;
  &lt;target name=&quot;run-tests&quot; depends=&quot;compile&quot; description=&quot;Runs test cases&quot;&gt;
      &lt;mkdir dir=&quot;${junit.results}&quot;/&gt;

      &lt;junit printsummary=&quot;no&quot; haltonfailure=&quot;yes&quot; haltonerror=&quot;yes&quot;
fork=&quot;yes&quot; dir=&quot;../test&quot;&gt;
          &lt;classpath&gt;
             &lt;path refid=&quot;runtime-classpath&quot;/&gt;
          &lt;/classpath&gt;

	    &lt;sysproperty key=&quot;java.compiler&quot; value=&quot;NONE&quot; /&gt;
          &lt;sysproperty key=&quot;java.net.namelookup.cache&quot; value=&quot;0&quot; /&gt;
          &lt;sysproperty key=&quot;sun.net.inetaddr.ttl&quot; value=&quot;0&quot; /&gt;

          &lt;formatter type=&quot;plain&quot; usefile=&quot;no&quot;/&gt;
          &lt;formatter type=&quot;xml&quot; usefile=&quot;yes&quot;/&gt;

          &lt;batchtest todir=&quot;${junit.results}&quot;&gt;
              &lt;fileset dir=&quot;${src.dir}&quot;&gt;
                  &lt;include name=&quot;**/*Test.java&quot;/&gt;     
              &lt;/fileset&gt;
          &lt;/batchtest&gt;
      &lt;/junit&gt;
  &lt;/target&gt;

  &lt;!-- ==================================================================
--&gt;
  &lt;!-- Run an single testcase
--&gt;
  &lt;!-- ==================================================================
--&gt;
  &lt;target name=&quot;run-singletest&quot; depends=&quot;compile&quot; description=&quot;Runs a single
test case&quot;&gt;
      &lt;junit printsummary=&quot;no&quot; haltonfailure=&quot;yes&quot; haltonerror=&quot;yes&quot;
fork=&quot;yes&quot; dir=&quot;../test&quot;&gt;
          &lt;classpath&gt;
              &lt;path refid=&quot;runtime-classpath&quot;/&gt;
          &lt;/classpath&gt;

  	    &lt;sysproperty key=&quot;java.compiler&quot; value=&quot;NONE&quot; /&gt;
          &lt;sysproperty key=&quot;java.net.namelookup.cache&quot; value=&quot;0&quot; /&gt;
          &lt;sysproperty key=&quot;sun.net.inetaddr.ttl&quot; value=&quot;0&quot; /&gt;

          &lt;formatter type=&quot;plain&quot; usefile=&quot;false&quot;/&gt;

          &lt;test name=&quot;${testcase}&quot;/&gt;
      &lt;/junit&gt;
  &lt;/target&gt;

