import org.junit.runner.JUnitCore;

import org.junit.runner.Result;

import org.junit.runner.notification.Failure;

import org.junit.runner.notification.RunListener;

import org.slf4j.Logger;

import org.slf4j.LoggerFactory;



public class Test3

{

   // Create logback logger instance

   protected static Logger logger = LoggerFactory

                           .getLogger( Test3.class );





   public static void main(String[] args)

   {

               JUnitCore core = new JUnitCore();

               core.addListener( new RunListener() {

                           @Override

                           public void testFailure(Failure failure)

                                                   throws Exception

                           {

                                       super.testFailure( failure );

                                       log( failure );

                           }





                           @Override

                           public void testRunFinished(Result result)

                                                   throws Exception

                           {

                                       super.testRunFinished( result );

                                       log(result);

                           }

               } );

               core.run( TestSuite.class );

   }





   private static void log(Failure failure)

   {

               logger.error( &quot;Test failed: {}&quot;, failure.getDescription() );

               logger.error( &quot;Test failure exception: {}&quot;,
failure.getException().toString() );               

   }





   private static void log(Result result)

   {

               logger.info( &quot;Number of failures: {}&quot;,
result.getFailureCount() );

               logger.info( &quot;Number of ignores: {}&quot;, result.getIgnoreCount()
);

               logger.info( &quot;Number of tests run: {}&quot;, result.getRunCount()
);

               logger.info( &quot;Runtime: {}&quot;, result.getRunTime() );

               logger.info( &quot;Test successful: {}&quot;, result.wasSuccessful() );

   }

}







