 &gt;        h1.put(&quot;k1&quot;,d1);
 &gt;        this.mgr.addDelegatedRoles(droles,&quot;Application&quot;,&quot;Prime 
group:Prime portal&quot;,h1,&quot;Default Domain&quot;);
 &gt;        }
