&gt; Hi,
&gt;
&gt; You can test applets with JFCUnit, an extension for Swing.
&gt;
&gt; A sample is attached below.
&gt;
&gt; CS (Germany)
&gt;
&gt;
&gt;
&gt;
&gt; --------------------------------------------
&gt; !!! SAMPLE WITH MOCKOBJECTS !!!
&gt;
&gt; import junit.extensions.jfcunit.JFCTestCase;
&gt; import junit.extensions.jfcunit.JFCTestHelper;
&gt; import org.easymock.EasyMock;
&gt; import org.easymock.MockControl;
&gt;
&gt; import javax.swing.*;
&gt; import java.util.*;
&gt;
&gt; public class ReportFilterAppletTest extends JFCTestCase {
&gt;
&gt;   private JFCTestHelper helper = null;
&gt;   private JFrame frame = null;
&gt;   private MyApplet applet = null;
&gt;
&gt;   public MyAppletTest( String name ) {
&gt;     super( name );
&gt;   }
&gt;
&gt;   public void setUp() {
&gt;
&gt;     /*-- initialize
&gt;               --*/
&gt;     helper = new JFCTestHelper();
&gt;
&gt;     /*-- JFrame und Applet
&gt;                --*/
&gt;     frame = new JFrame();
&gt;     frame.setName( &quot;JFrame&quot; );
&gt;     applet = new MyApplet( appletMock, &quot;JFrame&quot; );
&gt;
&gt;
&gt;   }
&gt;
&gt;   public void tearDown() {
&gt;     /*-- Fenster-Ressourcen nach jedem Test wieder
&gt;           --*/
&gt;         helper.cleanUp(this);
&gt;   }
&gt;
&gt;   /**
&gt;    * Testet Ausfuhrungsrecht
&gt;    */
&gt;   public void testIt()
&gt;     throws Exception {
&gt;
&gt;     applet.init();
&gt;     frame.setContentPane( applet.getContentPane() );
&gt;     frame.setSize( 600, 800 );
&gt;     frame.show();
&gt;
&gt;     /*-- BEGIN Test --*/
&gt;
&gt;     /*-- Teste, ob die Applet-ContentPane umbennannt
&gt;       --*/
&gt;     Component component = findComponent( &quot;contenpane&quot; );
&gt;     junit.framework.Assert.assertNotNull( &quot;contenpane is null&quot;, 
&gt; component );
&gt;     assertTrue( flag, componentName, component );
&gt;
&gt;     /*-- END Test --*/
&gt;
&gt;   }
&gt;
&gt;
