         IAMSDBHome home = (IAMSDBHome)getInitialContext()
           .lookup(getJNDIProperty(&quot;IAMSDBBean&quot;));
         theInstance = home.create();
       }
       catch(Exception e)
       {
         throw new NestedException(&quot;Failed to find IAMSDB&quot;, e);
       } 
