&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt; ============= build.xml begin
&gt; &gt; &gt; &gt; &lt;?xml version=&quot;1.0&quot;?&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt; &lt;project name=&quot;xfiles&quot; default=&quot;jar&quot; basedir=&quot;.&quot;&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;init&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;name&quot; value=&quot;xfiles&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;src.dir&quot; value=&quot;src&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;lib.dir&quot; value=&quot;lib&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;build.dir&quot; value=&quot;build&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;dist.dir&quot; value=&quot;dist&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;build.compiler&quot; value=&quot;classic&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;property name=&quot;junit.dir&quot; value=&quot;../junit3.5/junit.jar&quot; /&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;JUNIT&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;available classname=&quot;junit.framework.TestCase&quot;
&gt; &gt; &gt; &gt; classpath=&quot;${junit.dir}/&quot; property=&quot;junit.present&quot; /&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;clean&quot; depends=&quot;init&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;delete dir=&quot;${build.dir}&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;delete dir=&quot;${dist.dir}&quot; /&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;prepare&quot; depends=&quot;init&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;mkdir dir=&quot;${build.dir}&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;mkdir dir=&quot;${build.dir}/META-INF&quot; /&gt;
&gt; &gt; &gt; &gt;     &lt;mkdir dir=&quot;${dist.dir}&quot; /&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;compile&quot; depends=&quot;prepare,JUNIT&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;javac srcdir=&quot;${src.dir}&quot;
&gt; &gt; &gt; &gt;            destdir=&quot;${build.dir}/&quot;
&gt; &gt; &gt; &gt;            classpath=&quot;${lib.dir}/:${junit.dir}/&quot;
&gt; &gt; &gt; &gt;            includes=&quot;**&quot; /&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;runtests&quot; depends=&quot;compile&quot; if=&quot;junit.present&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;java fork=&quot;yes&quot; classname=&quot;junit.textui.TestRunner&quot;
&gt; &gt; &gt; &gt;       taskname=&quot;junit&quot; failonerror=&quot;true&quot;&gt;
&gt; &gt; &gt; &gt;       &lt;arg value=&quot;se.offex.jnitnpapi.MarketTest&quot;/&gt;
&gt; &gt; &gt; &gt;       &lt;classpath&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement location=&quot;${build.dir}&quot;/&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement path=&quot;${junit.dir}/&quot; /&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement path=&quot;&quot;/&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement path=&quot;${java.class.path}&quot;/&gt;
&gt; &gt; &gt; &gt;       &lt;/classpath&gt;
&gt; &gt; &gt; &gt;     &lt;/java&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;launchjunit&quot; depends=&quot;compile&quot; if=&quot;junit.present&quot;&gt;
&gt; &gt; &gt; &gt;     &lt;java fork=&quot;yes&quot; classname=&quot;junit.swingui.TestRunner&quot;
&gt; &gt; &gt; &gt;       taskname=&quot;junit&quot; failonerror=&quot;true&quot;&gt;
&gt; &gt; &gt; &gt;       &lt;classpath&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement location=&quot;${build.dir}&quot;/&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement path=&quot;${junit.dir}/&quot; /&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement path=&quot;&quot;/&gt;
&gt; &gt; &gt; &gt;         &lt;pathelement path=&quot;${java.class.path}&quot;/&gt;
&gt; &gt; &gt; &gt;       &lt;/classpath&gt;
&gt; &gt; &gt; &gt;     &lt;/java&gt;
&gt; &gt; &gt; &gt;   &lt;/target&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt;   &lt;target name=&quot;lj&quot; depends=&quot;launchjunit&quot;/&gt;
&gt; &gt; &gt; &gt;
&gt; &gt; &gt; &gt; &lt;/project&gt;
&gt; &gt; &gt; &gt; ============= build.xml end
