public class DomAssertTest extends TestCase {

    private static final String XHTML_DOCTYPE = &quot;&lt;!DOCTYPE html PUBLIC
&#92;&quot;-//W3C//DTD XHTML 1.0 Strict//EN&#92;&quot; &#92;&quot;test/dtd/xhtml1-strict.dtd&#92;&quot;&gt;&quot;;

    private boolean success;
    private String expected;
    private String actual;
    private String message;

    public DomAssertTest(boolean success, String message, String expected,
String actual) {
        super(&quot;execute&quot;);

        this.success = success;
        this.message = message;
        this.expected = expected;
        this.actual = actual;
    }

    public static Test suite() {
        TestSuite suite = new TestSuite();

        suite.addTest(new DomAssertTest(true, &quot;empty expected (long/long
forms)&quot;, &quot;&lt;el&gt;&lt;/el&gt;&quot;, &quot;&lt;el&gt;&lt;/el&gt;&quot;));

        // about 60 other boring tests to write....

        return suite;
    }

    public void execute() throws Exception {
        if (this.success) {
            DomAssert.assertEquals(this.message,
DomUtil.asDocument(this.expected), DomUtil.asDocument(this.actual));
        } else {
            try {
                DomAssert.assertEquals(this.message,
DomUtil.asDocument(this.expected), DomUtil.asDocument(this.actual));
            } catch (AssertionFailedError expected) {
                return;
            }
            fail();
        }
    }

    public String getName() {
        if (this.message != null) {
            if (success) {
                return &quot;success &quot; + this.message;
            } else {
                return &quot;fail &quot; + this.message;
            }
        } else {
            return super.getName();
        }
    }

}

