My tests are compiled independently and run on the *.jar of the src.

Here&#39;s one of my build files:

&lt;project name=&quot;WaterObjectsModel&quot; default=&quot;zipdistfull&quot; basedir=&quot;.&quot;&gt;
    &lt;description&gt;
        General build file for java projects.
        The project name is used to name jar files, junit reports and so on.
        Use CreateNewProject.bat to create the permanent folders and a copy
of this in the project root.
    &lt;/description&gt;

    &lt;!-- set global properties for this build --&gt;
    &lt;property name=&quot;project.name&quot;         value=&quot;WaterObjectsModel&quot;/&gt;
    &lt;property name=&quot;test.length&quot;          value=&quot;short&quot;/&gt;          &lt;!--
short/long --&gt;
    &lt;property name=&quot;javadoc.package&quot;      value=&quot;org.waterobjects.*&quot;/&gt;
    &lt;property name=&quot;testtorun&quot;
value=&quot;org.waterobjects.AllTests&quot;/&gt;

    &lt;!-- set global location properties for this build --&gt;
    &lt;property name=&quot;src&quot;               location=&quot;src&quot;/&gt;
    &lt;property name=&quot;lib&quot;               location=&quot;lib&quot;/&gt;
    &lt;property name=&quot;samples&quot;           location=&quot;samples&quot;/&gt;
    &lt;property name=&quot;documentation&quot;     location=&quot;documentation&quot;/&gt;
    &lt;property name=&quot;tests&quot;             location=&quot;tests&quot;/&gt;
    &lt;property name=&quot;testlib&quot;           location=&quot;testlib&quot;/&gt;
    &lt;property name=&quot;testdata&quot;          location=&quot;testdata&quot;/&gt;
    &lt;property name=&quot;testdata.runbasis&quot; location=&quot;${testdata}/runbasis&quot;/&gt;
    &lt;property name=&quot;testdata.input&quot;    location=&quot;${testdata}/input&quot;/&gt;
    &lt;property name=&quot;testdata.change&quot;   location=&quot;${testdata}/change&quot;/&gt;
    &lt;property name=&quot;testdata.good&quot;     location=&quot;${testdata}/good&quot;/&gt;
    &lt;property name=&quot;testdata.output&quot;   location=&quot;${testdata}/output&quot;/&gt;
    &lt;property name=&quot;testrun&quot;           location=&quot;testrun&quot;/&gt;
    &lt;property name=&quot;reports&quot;           location=&quot;reports&quot;/&gt;
    &lt;property name=&quot;build&quot;             location=&quot;build&quot;/&gt;
    &lt;property name=&quot;build.srcjar&quot;      location=&quot;${build}/srcjar&quot;/&gt;
    &lt;property name=&quot;build.classes&quot;     location=&quot;${build}/classes&quot;/&gt;
    &lt;property name=&quot;build.dist&quot;        location=&quot;${build}/dist&quot;/&gt;
    &lt;property name=&quot;keystore&quot;          location=&quot;${build}/keystore&quot;/&gt;
    &lt;property name=&quot;build.javadocs&quot;    location=&quot;${build}/javadocs&quot;/&gt;
    &lt;property name=&quot;build.testclasses&quot; location=&quot;${build}/testclasses&quot;/&gt;
    &lt;property name=&quot;build.testjar&quot;     location=&quot;${build}/testjar&quot;/&gt;
    &lt;property name=&quot;zippeddist&quot;        location=&quot;zippeddist&quot;/&gt;
    &lt;property name=&quot;zippeddistfull&quot;    location=&quot;zippeddistfull&quot;/&gt;

    &lt;path id=&quot;libs&quot;&gt;
        &lt;fileset dir=&quot;${lib}&quot; includes=&quot;**/*.jar&quot;/&gt;
    &lt;/path&gt;

    &lt;path id=&quot;testlibs&quot;&gt;
        &lt;path refid=&quot;libs&quot;/&gt;
        &lt;fileset dir=&quot;${testlib}&quot; includes=&quot;**/*.jar&quot;/&gt;
    &lt;/path&gt;

    &lt;target name=&quot;init&quot; description=&quot;Intialization&quot;&gt;
        &lt;!-- Start recording to log --&gt;
        &lt;record name=&quot;buildlog.txt&quot; loglevel=&quot;info&quot; append=&quot;no&quot;/&gt;
        &lt;!-- Create the time stamp --&gt;
        &lt;tstamp/&gt;
        &lt;!-- Define the time stamp dependent properties --&gt;
        &lt;property name=&quot;reports.dir&quot;
location=&quot;${reports}/${DSTAMP}${TSTAMP}&quot;/&gt;
        &lt;property name=&quot;zippeddist.dir&quot;     location=&quot;${zippeddist}&quot;/&gt;
        &lt;property name=&quot;zippeddistfull.dir&quot; location=&quot;${zippeddistfull}&quot;/&gt;
        &lt;property name=&quot;project.jar&quot;
location=&quot;${build.dist}/${project.name}.jar&quot;/&gt;
        &lt;property name=&quot;project.signedjar&quot;
location=&quot;${build.dist}/${project.name}Signed.jar&quot;/&gt;
    &lt;/target&gt;

    &lt;target name=&quot;clean&quot; depends=&quot;init&quot; description=&quot;Deletes old
directories&quot;&gt;
        &lt;!-- Deletes all directories that are created by this build.xml --&gt;
        &lt;delete dir=&quot;${build}&quot;/&gt;
        &lt;delete dir=&quot;${testrun}&quot;/&gt;
        &lt;delete dir=&quot;${testdata.output}&quot;/&gt;
        &lt;delete dir=&quot;${reports.dir}&quot;/&gt;
        &lt;delete dir=&quot;${zippeddist.dir}&quot;/&gt;
        &lt;delete dir=&quot;${zippeddistfull.dir}&quot;/&gt;
        &lt;delete&gt;
            &lt;fileset dir=&quot;.&quot;&gt;
                &lt;include name=&quot;TEST-*.xml&quot;/&gt;
            &lt;/fileset&gt;
        &lt;/delete&gt;
    &lt;/target&gt;

        &lt;!--
        &lt;mkdir dir=&quot;${build.javadocs}&quot;/&gt;
        --&gt;

    &lt;target name=&quot;compile&quot; depends=&quot;clean&quot; description=&quot;Compile the source&quot;
&gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${build}&quot;/&gt;
        &lt;mkdir dir=&quot;${build.classes}&quot;/&gt;

        &lt;!-- Compile the java code --&gt;
        &lt;javac srcdir=&quot;${src}&quot; destdir=&quot;${build.classes}&quot;&gt;
            &lt;classpath refid=&quot;libs&quot; /&gt;
        &lt;/javac&gt;
    &lt;/target&gt;

    &lt;target name=&quot;dist&quot; depends=&quot;compile&quot;
        description=&quot;Generate the jar file&quot; &gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${build.dist}&quot;/&gt;

        &lt;jar jarfile=&quot;${project.jar}&quot; basedir=&quot;${build.classes}&quot;&gt;
            &lt;include name=&quot;**/*.class&quot;/&gt;
        &lt;/jar&gt;
    &lt;/target&gt;

    &lt;target name=&quot;genkey&quot; depends=&quot;dist&quot;
        description=&quot;Genrate an authetification key for the applet&quot;&gt;
        &lt;genkey
            alias=&quot;PH-Consult&quot;
            keystore=&quot;${keystore}&quot;
            keypass=&quot;kpi5491chp&quot;
            dname=&quot;CN=PH-Consult, O=www.phc.dk, C=DK&quot;
            storepass=&quot;p19h45c&quot;&gt;
        &lt;/genkey&gt;
    &lt;/target&gt;

    &lt;target name=&quot;signjar&quot; depends=&quot;genkey&quot;
        description=&quot;Sign the jar file for use as applet&quot;&gt;
        &lt;signjar
            jar=&quot;${project.jar}&quot;
            keystore=&quot;${keystore}&quot;
            storepass=&quot;p19h45c&quot;
            keypass=&quot;kpi5491chp&quot;
            signedjar=&quot;${project.signedjar}&quot;
            alias=&quot;PH-Consult&quot;&gt;
        &lt;/signjar&gt;
    &lt;/target&gt;

    &lt;target name=&quot;compiletests&quot; depends=&quot;signjar&quot;
        description=&quot;Compile the tests &quot; &gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${build.testclasses}&quot;/&gt;

        &lt;javac srcdir=&quot;${tests}&quot; destdir=&quot;${build.testclasses}&quot;&gt;
            &lt;classpath refid=&quot;testlibs&quot;/&gt;
            &lt;classpath path=&quot;${project.jar}&quot;/&gt;
        &lt;/javac&gt;
    &lt;/target&gt;

    &lt;target name=&quot;test&quot; depends=&quot;dist,compiletests&quot;
        description=&quot;Test the distribution with JUnit&quot; &gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${testdata.output}&quot;/&gt;
        &lt;mkdir dir=&quot;${testrun}&quot;/&gt;

        &lt;!-- Copy the change data files that will be changed to the output
directory --&gt;
        &lt;copy todir=&quot;${testdata.output}&quot;&gt;
            &lt;fileset dir=&quot;${testdata.change}&quot; includes=&quot;**/*&quot; /&gt;
            &lt;fileset dir=&quot;${testdata.input}&quot;  includes=&quot;**/*.&quot; /&gt;
        &lt;/copy&gt;

        &lt;!-- Copy the change data files that will be changed to the output
directory --&gt;
        &lt;copy todir=&quot;${testrun}&quot;&gt;
            &lt;fileset dir=&quot;${testdata.runbasis}&quot; includes=&quot;**/*&quot; /&gt;
        &lt;/copy&gt;

        &lt;!-- Run tests with JUnit --&gt;
        &lt;junit fork=&quot;on&quot; dir=&quot;${build}&quot; showoutput=&quot;true&quot;&gt;
            &lt;classpath refid=&quot;testlibs&quot; /&gt;
            &lt;classpath path=&quot;${build.testclasses}&quot; /&gt;
            &lt;classpath path=&quot;${project.jar}&quot; /&gt;
            &lt;formatter type=&quot;xml&quot; /&gt;
            &lt;jvmarg line=&quot;-Xss8192k&quot; /&gt;
            &lt;test name=&quot;${testtorun}&quot; /&gt;
            &lt;sysproperty key=&quot;user.dir&quot; value=&quot;${testrun}&quot;/&gt;
        &lt;/junit&gt;
    &lt;/target&gt;

    &lt;target name=&quot;report&quot; depends=&quot;test&quot;
        description=&quot;Transform test results from xml to html&quot;&gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${reports.dir}&quot;/&gt;

        &lt;!-- Copy the XML output to report directory --&gt;
        &lt;copy todir=&quot;${reports.dir}&quot;&gt;
            &lt;fileset dir=&quot;.&quot;&gt;
                &lt;include name=&quot;TEST-*.xml&quot;/&gt;
            &lt;/fileset&gt;
        &lt;/copy&gt;

        &lt;junitreport todir=&quot;${reports.dir}&quot;&gt;
            &lt;fileset dir=&quot;${reports.dir}&quot;&gt;
                &lt;include name=&quot;TEST-*.xml&quot;/&gt;
            &lt;/fileset&gt;
            &lt;report format=&quot;noframes&quot; todir=&quot;${reports.dir}&quot;/&gt;
        &lt;/junitreport&gt;
    &lt;/target&gt;

    &lt;target name=&quot;javadocs&quot; depends=&quot;report&quot;
        description=&quot;Generate java docs for the source (excluding tests)&quot; &gt;

		&lt;javadoc sourcepath=&quot;${src}&#92;**/*&quot; destdir=&quot;${build.javadocs}&quot;&gt;
			&lt;fileset dir=&quot;${src}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;group title=&quot;${project.name}&quot; packages=&quot;${javadoc.package}&quot;/&gt;
		&lt;/javadoc&gt;
    &lt;/target&gt;

	&lt;target name=&quot;zipdist&quot; depends=&quot;report&quot;
		description=&quot;Creates a zip file that could be sent directly to a user.&quot;&gt;

		&lt;zip destfile=&quot;${project.name}${DSTAMP}${TSTAMP}.zip&quot; filesonly=&quot;true&quot;&gt;
			&lt;fileset dir=&quot;${build.dist}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;!-- Is this needed or could external *.jar files be included in the
application *.jar --&gt;
			&lt;fileset dir=&quot;${lib}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${samples}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${documentation}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;.&quot; includes=&quot;*.lnk&quot; /&gt;
			&lt;fileset dir=&quot;.&quot; includes=&quot;*.bat&quot; /&gt;
		&lt;/zip&gt;
	&lt;/target&gt;

    &lt;target name=&quot;srcjar&quot; depends=&quot;zipdist&quot;
        description=&quot;Generate the jar file of the source java files&quot; &gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${build.srcjar}&quot;/&gt;

        &lt;jar jarfile=&quot;${build.srcjar}&#92;src.jar&quot; basedir=&quot;${src}&quot;&gt;
            &lt;include name=&quot;**/*.java&quot;/&gt;
        &lt;/jar&gt;
    &lt;/target&gt;

    &lt;target name=&quot;testjar&quot; depends=&quot;srcjar&quot;
        description=&quot;Generate the jar file of the test java files&quot; &gt;

        &lt;!-- Create directories --&gt;
        &lt;mkdir dir=&quot;${build.testjar}&quot;/&gt;

        &lt;jar jarfile=&quot;${build.testjar}&#92;tests.jar&quot; basedir=&quot;${tests}&quot;&gt;
            &lt;include name=&quot;**/*.java&quot;/&gt;
        &lt;/jar&gt;
    &lt;/target&gt;

	&lt;target name=&quot;zipdistfull&quot; depends=&quot;testjar&quot;
		description=&quot;Creates a zip file that could be archived or used by a
developer.&quot;&gt;
		&lt;zip
destfile=&quot;${zippeddistfull.dir}&#92;${project.name}${DSTAMP}${TSTAMP}_sdk.zip&quot;&gt;
			&lt;fileset dir=&quot;${build.dist}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${build.srcjar}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${build.testjar}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${build.javadocs}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${lib}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${testlib}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${samples}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${documentation}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;${reports.dir}&quot; includes=&quot;**/*&quot; /&gt;
			&lt;fileset dir=&quot;.&quot; includes=&quot;*.lnk&quot; /&gt;
		&lt;/zip&gt;
	&lt;/target&gt;

&lt;/project&gt;


