&gt; I&#39;ve used JFCUnit a little bit.  Here are some test methods I wrote:
&gt; 
&gt;     private Component assertNotNullAndShowing(String name, Container
&gt; container, int index)
&gt;     {
&gt;         Component component = m_helper.findNamedComponent(name,
&gt; container, index);
&gt;         assertNotNull(&quot;Could not find &quot; + name, component);
&gt;         assertTrue(name + &quot; is not showing&quot;, component.isShowing());
&gt;         
&gt;         return component;
&gt;     }
&gt; 
&gt;     public void testComponents()
&gt;     {
&gt;         //Check to make sure all the visible components are showing.
&gt;         
&gt;         assertNotNullAndShowing(&quot;AddButton&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;DeleteButton&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;ConnectButton&quot;, m_dialog, 0);
&gt;         
&gt;         assertNotNullAndShowing(&quot;SelectADeviceLabel&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;ScrollPane&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;Table&quot;, m_dialog, 0);
&gt;         
&gt;         assertNotNullAndShowing(&quot;Separator&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;AccessLevel&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;GuestRadio&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;OperatorRadio&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;AdministratorRadio&quot;, m_dialog, 0);
&gt;         
&gt;         assertNotNullAndShowing(&quot;PasswordLabel&quot;, m_dialog, 0);
&gt;         assertNotNullAndShowing(&quot;PasswordField&quot;, m_dialog, 0);
&gt;     }
&gt;         
&gt;     public void testAddClick() throws Exception
&gt;     {
&gt;         JButton addButton =
&gt; (JButton)m_helper.findNamedComponent(&quot;AddButton&quot;, m_dialog, 0);
&gt;         assertNotNull(&quot;Could not find the Add button&quot;, addButton);
&gt;         
&gt;         m_helper.enterClickAndLeave(new MouseEventData(this,
&gt; addButton));
&gt;         
&gt;         //verify that the add dialog is shown.
&gt;         //then close it.
&gt;         java.util.List showingDialogs = m_helper.getShowingDialogs
(&quot;Add
&gt; Device&quot;);
&gt;         assertEquals(&quot;Number of dialogs showing is wrong&quot;, 1,
&gt; showingDialogs.size());
&gt;         
&gt;         JDialog addDialog = ( JDialog )showingDialogs.get( 0 );
&gt;         assertEquals(&quot;Wrong dialog showing up&quot;, &quot;Add Device&quot;,
&gt; addDialog.getTitle());
&gt;         m_helper.disposeWindow(addDialog, this);
&gt;     }
&gt; 
&gt;     public void testAddOneDevice() throws Exception
&gt;     {
&gt;         JButton addButton =
&gt; (JButton)m_helper.findNamedComponent(&quot;AddButton&quot;, m_dialog, 0);
&gt;         assertNotNull(&quot;Could not find the Add button&quot;, addButton);
&gt;         
&gt;         m_helper.enterClickAndLeave(new MouseEventData(this,
&gt; addButton));
&gt;         
&gt;         //verify that the add dialog is shown.
&gt;         //then close it.
&gt;         java.util.List showingDialogs = m_helper.getShowingDialogs
(&quot;Add
&gt; Device&quot;);
&gt;         assertEquals(&quot;Number of dialogs showing is wrong&quot;, 1,
&gt; showingDialogs.size());
&gt;         
&gt;         JDialog addDialog = ( JDialog )showingDialogs.get( 0 );
&gt;         assertEquals(&quot;Wrong dialog showing up&quot;, &quot;Add Device&quot;,
&gt; addDialog.getTitle());
&gt;         
&gt;         //get the text fields
&gt;         JTextField deviceNameField =
&gt; (JTextField)assertNotNullAndShowing(&quot;DeviceNameField&quot;, addDialog, 
0);
&gt;         JTextField addressField =
&gt; (JTextField)assertNotNullAndShowing(&quot;AddressField&quot;, addDialog, 0);
&gt;         JButton addButton2 =
&gt; (JButton)assertNotNullAndShowing(&quot;AddButton&quot;, addDialog, 0);
&gt;         
&gt;         //put text in the text fields.
&gt;         deviceNameField.requestFocus();
&gt;         m_helper.sendString(new StringEventData(this, 
deviceNameField,
&gt; &quot;TestDeviceName&quot;));
&gt;         
&gt;         addressField.requestFocus();
&gt;         m_helper.sendString(new StringEventData(this, addressField,
&gt; &quot;TestDeviceAddress&quot;));
&gt;        
&gt;         //then click the add button.
&gt;         m_helper.enterClickAndLeave(new MouseEventData(this,
&gt; addButton2));
&gt;         
&gt;         //verify that the dialog closed.
&gt;         showingDialogs = m_helper.getShowingDialogs(&quot;Add Device&quot;);
&gt;         assertEquals(&quot;Number of dialogs showing is wrong&quot;, 0,
&gt; showingDialogs.size());
&gt; 
&gt;         //verify that the item was added to the table.
&gt;         //get the table
&gt;         JTable table = (JTable)assertNotNullAndShowing(&quot;Table&quot;,
&gt; m_dialog, 0);
&gt;         TableModel model = table.getTableModel();
&gt;         int rowCount = model.getRowCount();
&gt;         assertTrue(rowCount &gt;= 1);
&gt;         
&gt;         String name = (String)model.getValueAt(rowCount-1, 0);
&gt;         String address = (String)model.getValueAt(rowCount-1, 1);
&gt;         
&gt;         assertEquals(name, &quot;TestDeviceName&quot;);
&gt;         assertEquals(address, &quot;TestDeviceAddress&quot;);
&gt;     }
