public class ThreadTest extends TestCase
{
   class Server extends Thread
   {
      public void run()
      {
         fail(&quot;The server failed its test case&quot;);

         synchronized (this)
         {
            notifyAll();
         }
      }
   }

   Server server;

   public ThreadTest(String name)
   {
      super(name);

      server = new Server();
   }

   protected void setUp()
   {
      try
      {
         server.start();

         synchronized (server)
         {
            server.wait();
         }
      }
      catch (Exception e)
      {
         fail(e.toString());
      }
   }

   public void testSomething() {}
}

