  &lt;/description&gt;

  &lt;!-- Set up global properties --&gt;

  &lt;!-- When building ALL of EnterpriseConnect, this property will be 
set to the location --&gt;
  &lt;!-- where all build artifacts will be kept. Developer&#39;s should not 
reset this property --&gt;
  &lt;property name=&quot;build.ec.location&quot; value=&quot;.&quot;/&gt;

  &lt;!-- All build artifacts for all components are placed under this 
directory. --&gt;
  &lt;property name=&quot;build.dir&quot; value=&quot;${build.ec.location}/build&quot;/&gt;

  &lt;!-- The source code for this component can be found in this 
directory --&gt;
  &lt;property name=&quot;src.dir&quot; value=&quot;${basedir}/GeneralUtilities&quot;/&gt;

  &lt;!-- The directory resembling the package name. This is needed 
because when the --&gt;
  &lt;!-- jcoverage reports are generated, it is expected that the JAVA 
code is actually --&gt;
  &lt;!-- in the ${src.dir}/&lt;package name&gt; directory. --&gt;
  &lt;property name=&quot;src.temp.dir&quot; 
value=&quot;${src.dir}/com/aspentech/aep/ec/utils&quot;/&gt;

  &lt;!-- The build artifacts for this particular utility are placed 
under this directory. --&gt;
  &lt;property name=&quot;build.specific.dir&quot; 
value=&quot;${build.dir}/GeneralUtilities/bw&quot;/&gt;

  &lt;!-- The classes generated by the javac compiler are placed in this 
directory. --&gt;
  &lt;property name=&quot;build.classes.dir&quot; 
value=&quot;${build.specific.dir}/classes&quot;/&gt;

  &lt;!-- The instrumented classes are placed in this directory. --&gt;
  &lt;property name=&quot;build.instrumented.dir&quot; 
value=&quot;${build.specific.dir}/instrumented-classes&quot;/&gt;

  &lt;!-- Coverage reports are placed in this directory. For the HTML 
report, --&gt;
  &lt;!-- look at ${build.coverage.dir}/index.html. For the XML report, 
look --&gt;
  &lt;!-- at ${build.coverage.dir}/coverage.xml. --&gt;
  &lt;property name=&quot;build.coverage.dir&quot; 
value=&quot;${build.specific.dir}/coverage&quot;/&gt;

  &lt;!-- Unit test reports from junit are placed in this directory. The 
type and location of --&gt;
  &lt;!-- the report depends on the setting of the ${junit.format.type) 
property: --&gt;
  &lt;!--     brief: only prints detailed information for test cases 
that failed (THE DEFAULT) --&gt;
  &lt;!--     plain: prints a little statistics line for each test case -
-&gt;
  &lt;!--     xml:   prints an XML file, which can then be used 
by &#39;junitreport&#39; --&gt;
  &lt;!-- The text report is placed under the 
${build.specific.dir}/reports/text directory. --&gt;
  &lt;!-- The html report is placed under the 
${build.specific.dir}/reports/html directory. --&gt;
  &lt;property name=&quot;build.reports.dir&quot; 
value=&quot;${build.specific.dir}/reports&quot;/&gt;

  &lt;!-- The format type of the report generated for the JUNIT tests. 
To override this format --&gt;
  &lt;!-- reset during build process. --&gt;
  &lt;property name=&quot;junit.format.type&quot; value=&quot;brief&quot;/&gt;

  &lt;!-- This variable is used to determine whether or not 
the &#39;junitreport&#39; target should be --&gt;
  &lt;!-- executed. If the ${junit.format.type} property was set 
to &#39;xml&#39; the target will be --&gt;
  &lt;!-- executed; otherwise, it will not. --&gt;
  &lt;property name=&quot;${junit.format.type}&quot; value=&quot;&quot;/&gt;

  &lt;!-- Java docs for the application classes are placed in this 
directory. --&gt;
  &lt;property name=&quot;build.jdocs.dir&quot; 
value=&quot;${build.specific.dir}/jdocs&quot;/&gt;

  &lt;!-- The resulting JAR file created for this component. --&gt;
  &lt;property name=&quot;utilsgeneralutilities.jar&quot; 
value=&quot;${build.specific.dir}/utilsGeneralUtilities.jar&quot;/&gt;

  &lt;!-- This is needed to compile the code, since it uses logging. --&gt;
  &lt;property name=&quot;tibco.dir&quot; location=&quot;c:/tibco&quot;/&gt;
  &lt;property name=&quot;log4j.jar&quot; 
location=&quot;${tibco.dir}/tra/1.0/lib/ext/log4j.jar&quot;/&gt;
  &lt;!-- Define variable for debugging task, and set to &#39;true&#39; by 
default. --&gt;
  &lt;property name=&quot;debug&quot; value=&quot;true&quot;/&gt;

  &lt;!-- Make available the ANT tasks for jcoverage --&gt;
  &lt;property environment=&quot;env&quot;/&gt;
  &lt;path id=&quot;jcoverage&quot;&gt;
    &lt;fileset dir=&quot;${env.ANT_HOME}/lib/jcoverage-1.0.5&quot;&gt;
      &lt;include name=&quot;jcoverage.jar&quot;/&gt;
    &lt;/fileset&gt;
  &lt;/path&gt;
  &lt;taskdef classpathref=&quot;jcoverage&quot; resource=&quot;tasks.properties&quot;/&gt;

  &lt;!-- This is the distribution target. Running &#39;ant&#39; with no target 
name specified --&gt;
  &lt;!-- will execute this target by default. --&gt;
  &lt;target name=&quot;dist&quot; 
depends=&quot;package,junithtmlreport,junittxtreport,coveragereport&quot;/&gt;

  &lt;!-- This target handles the removal of all built artifacts. It 
removes the entire --&gt;
  &lt;!-- build directory, and removes the jcoverage.ser and 
jcoverage.log files. --&gt;
  &lt;target name=&quot;clean&quot; description=&quot;Clean up the build artifacts 
specific to this component.&quot;&gt;
    &lt;delete failonerror=&quot;false&quot; includeEmptyDirs=&quot;true&quot;&gt;
      &lt;fileset dir=&quot;${build.dir}&quot;/&gt;
      &lt;fileset dir=&quot;${src.dir}/com&quot;/&gt;
      &lt;fileset dir=&quot;${basedir}&quot;&gt;
        &lt;include name=&quot;jcoverage.ser&quot;/&gt;
	&lt;include name=&quot;jcoverage.log&quot;/&gt;
      &lt;/fileset&gt;
    &lt;/delete&gt;
  &lt;/target&gt;

  &lt;!-- This is the first target called. Its responsibility is to 
create the --&gt;
  &lt;!-- necessary build directories. --&gt;
  &lt;target name=&quot;init&quot; description=&quot;Create build directories specific 
to this component.&quot;&gt;
    &lt;mkdir dir=&quot;${build.dir}&quot;/&gt;
    &lt;mkdir dir=&quot;${build.classes.dir}&quot;/&gt;
    &lt;mkdir dir=&quot;${build.coverage.dir}&quot;/&gt;
    &lt;mkdir dir=&quot;${build.instrumented.dir}&quot;/&gt;
    &lt;mkdir dir=&quot;${build.jdocs.dir}&quot;/&gt;
    &lt;mkdir dir=&quot;${build.reports.dir}&quot;/&gt;
    &lt;mkdir dir=&quot;${build.specific.dir}&quot;/&gt;

    &lt;mkdir dir=&quot;${src.temp.dir}&quot;/&gt;
  &lt;/target&gt;

  &lt;!-- Compile the source code for this component. --&gt;
  &lt;target name=&quot;compile&quot; depends=&quot;init&quot; description=&quot;Compile all 
classes.&quot;&gt;
    &lt;javac
      debug=&quot;${debug}&quot;
      srcdir=&quot;${src.dir}&quot;
      destdir=&quot;${build.classes.dir}&quot;
      classpath=&quot;${log4j.jar}&quot;
      excludes=&quot;**/com/aspentech/aep/ec/utils/*.java&quot;
      failonerror=&quot;yes&quot;/&gt;
  &lt;/target&gt;

  &lt;!-- Create package for compiled source --&gt;
  &lt;target name=&quot;package&quot; depends=&quot;compile&quot; description=&quot;Package the 
application classes.&quot;&gt;
    &lt;jar jarfile=&quot;${utilsgeneralutilities.jar}&quot;&gt;
      &lt;manifest&gt;
        &lt;attribute name=&quot;Built-By&quot; value=&quot;${user.name}&quot;/&gt;
	&lt;attribute name=&quot;Implementation-Title&quot; 
value=&quot;utilsGeneralUtilities.jar&quot;/&gt;
	&lt;attribute name=&quot;Implementation-Vendor&quot; value=&quot;Aspen 
Technology, Inc.&quot;/&gt;
      &lt;/manifest&gt;
      &lt;fileset dir=&quot;${build.classes.dir}&quot;&gt;
        &lt;exclude name=&quot;**/*Test.class&quot;/&gt;
      &lt;/fileset&gt;
    &lt;/jar&gt;
  &lt;/target&gt;

  &lt;!-- Instrument the compiled classes, in preparation for performing 
coverage analysis --&gt;
  &lt;target name=&quot;instrument&quot; depends=&quot;compile&quot; description=&quot;Add 
jcoverage instrumentation.&quot;&gt;
    &lt;!-- Instrument the application classes, writing the 
instrumented --&gt;
    &lt;!-- classes to ${build.instrumented.dir}.  --&gt;
    &lt;instrument todir=&quot;${build.instrumented.dir}&quot;&gt;

      &lt;!-- Note that the following line causes instrument to ignore 
any --&gt;
      &lt;!-- source line containing a reference to log4j, for the 
purposes --&gt;
      &lt;!-- of coverage reporting. --&gt;
      &lt;ignore regex=&quot;org.apache.log4j.*&quot;/&gt;

      &lt;fileset dir=&quot;${build.classes.dir}&quot;&gt;
        &lt;!-- Instrument all the application classes, but don&#39;t 
instrument --&gt;
        &lt;!-- the test classes. --&gt;
        &lt;include name=&quot;**/*.class&quot;/&gt;
        &lt;exclude name=&quot;**/*Test.class&quot;/&gt;
      &lt;/fileset&gt;
    &lt;/instrument&gt;
  &lt;/target&gt;

  &lt;!-- Execute the set of unit tests for this component --&gt;
  &lt;target name=&quot;test&quot; depends=&quot;instrument&quot; description=&quot;Unit test the 
application.&quot;&gt;
    &lt;junit fork=&quot;yes&quot; dir=&quot;${basedir}&quot; printsummary=&quot;yes&quot; 
errorProperty=&quot;test.failed&quot; failureProperty=&quot;test.failed&quot;&gt;

      &lt;!-- Note the classpath order, instrumented classes are before 
the --&gt;
      &lt;!-- original (uninstrumented) classes. --&gt;
      &lt;classpath location=&quot;${build.instrumented.dir}&quot;/&gt;
      &lt;classpath location=&quot;${build.classes.dir}&quot;/&gt;

      &lt;!-- The instrumented classes reference classes used by the --&gt;
      &lt;!-- jcoverage runtime. --&gt;
      &lt;classpath refid=&quot;jcoverage&quot;/&gt;

      &lt;formatter type=&quot;${junit.format.type}&quot;/&gt;

      &lt;test name=&quot;${testcase}&quot; todir=&quot;${build.reports.dir}&quot; 
if=&quot;testcase&quot;/&gt;

      &lt;batchtest todir=&quot;${build.reports.dir}&quot; unless=&quot;testcase&quot;&gt;
        &lt;fileset dir=&quot;${src.dir}&quot;&gt;
          &lt;include name=&quot;**/*Test.java&quot;/&gt;
        &lt;/fileset&gt;
      &lt;/batchtest&gt;
    &lt;/junit&gt;
  &lt;/target&gt;

  &lt;!-- Generate HTML report from JUNIT test results, if configured. --
&gt;
  &lt;target name=&quot;junithtmlreport&quot; depends=&quot;test&quot; description=&quot;Generate 
JUNIT HTML report.&quot; if=&quot;xml&quot;&gt;
    &lt;junitreport todir=&quot;${build.reports.dir}&quot;&gt;
      &lt;fileset dir=&quot;${build.reports.dir}&quot;&gt;
        &lt;include name=&quot;TEST-*.xml&quot;/&gt;
      &lt;/fileset&gt;
      &lt;report format=&quot;frames&quot; todir=&quot;${build.reports.dir}/html&quot;/&gt;
    &lt;/junitreport&gt;
    &lt;echo&gt;
      junit report has been generated.
      The HTML report is ${build.reports.dir}/html/index.html
    &lt;/echo&gt;
  &lt;/target&gt;

  &lt;!-- Generate TEXT report from JUNIT test results, if configured. --
&gt;
  &lt;target name=&quot;junittxtreport&quot; depends=&quot;test&quot; description=&quot;Generate 
JUNIT TEXT report.&quot; unless=&quot;xml&quot;&gt;
    &lt;echo&gt;
      junit report has been generated.
      The TEXT report is ${build.reports.dir}/TEST-classname.txt
    &lt;/echo&gt;
  &lt;/target&gt;

  &lt;!-- Generate coverage reports from the results of JUNIT test 
execution. --&gt;
  &lt;target name=&quot;coveragereport&quot; depends=&quot;test&quot; description=&quot;HTML and 
XML coverage reports can be found in build/coverage&quot;&gt;
    &lt;copy todir=&quot;${src.temp.dir}&quot;&gt;
      &lt;fileset dir=&quot;${src.dir}&quot;&gt;
        &lt;include name=&quot;GeneralUtilities.java&quot;/&gt;
      &lt;/fileset&gt;
    &lt;/copy&gt;

    &lt;report srcdir=&quot;${src.dir}&quot; destdir=&quot;${build.coverage.dir}&quot;/&gt;
    &lt;report srcdir=&quot;${src.dir}&quot; destdir=&quot;${build.coverage.dir}&quot; 
format=&quot;xml&quot;/&gt;

    &lt;echo&gt;
      jcoverage reports have been generated.
      The HTML report is ${build.coverage.dir}/index.html
      The XML report is ${build.coverage.dir}/coverage.xml
    &lt;/echo&gt;
  &lt;/target&gt;

  &lt;!-- Generate Java documentation for source. --&gt;
  &lt;target name=&quot;jdocs&quot; depends=&quot;init&quot;&gt;
    &lt;javadoc 
      destdir=&quot;${build.jdocs.dir}&quot;
      author=&quot;true&quot;
      version=&quot;true&quot;
      use=&quot;true&quot;
      windowtitle=&quot;General Utilities BusinessWorks XPath Functions&quot;&gt;
      &lt;fileset dir=&quot;${src.dir}&quot; excludes=&quot;**/*Test.java, 
**/com/aspentech/aep/ec/utils/*.java&quot;/&gt;
    &lt;/javadoc&gt;
  &lt;/target&gt;


&lt;/project&gt;
