&gt; public static Test suite() {
&gt;   TestSuite suite = new TestSuite(&quot;MyTest&quot;);
&gt;   suite.addTest(new MyTest(&quot;test1&quot;));
&gt;   suite.addTest(new MyTest(&quot;test2&quot;));
&gt;   return suite;
&gt; }
