This is what I mean by a parameterized test case. The feature &quot;calculate 
a person&#39;s age by a given date.&quot; Each test is a single assertion, but 
since only the data changes, this test is data-driven, rather than 
duplicating the invocation of &quot;getAgeAsOf()&quot; in five tests.

-------------------
package ca.bluejays.scouting.amateur.model.test;

import java.util.Date;

import junit.framework.*;
import XXX.BiographicalReport;
import XXX.PlayerFactory;

import com.diasparsoftware.java.util.DateUtil;

public class CalculateAgeTest extends TestCase {
     private int expectedAge;
     private Date futureDate;

     public CalculateAgeTest(String testName, int expectedAge,
             Date futureDate) {

         super(testName);
         this.expectedAge = expectedAge;
         this.futureDate = futureDate;
     }

     public static Test suite() {
         TestSuite suite = new TestSuite(&quot;Calculate Age&quot;);
         suite.addTest(new CalculateAgeTest(&quot;birthday&quot;, 30, DateUtil
                 .makeDate(2004, 5, 4)));
         suite.addTest(new CalculateAgeTest(&quot;day before birthday&quot;, 29,
                 DateUtil.makeDate(2004, 5, 3)));
         suite.addTest(new CalculateAgeTest(&quot;day after birthday&quot;, 30,
                 DateUtil.makeDate(2004, 5, 5)));
         suite.addTest(
                 new CalculateAgeTest(&quot;date of birth&quot;, 0, DateUtil
                         .makeDate(1974, 5, 4)));
         suite.addTest(
                 new CalculateAgeTest(&quot;invalid future date&quot;, 0, DateUtil
                         .makeDate(1974, 5, 3)));
         return suite;
     }

     protected void runTest() throws Exception {
         BiographicalReport biographicalReport = PlayerFactory
                 .makeBiographicalReport(&quot;Joe&quot;, &quot;Rainsberger&quot;, DateUtil
                         .makeDate(1974, 5, 4));

         assertEquals(new Integer(expectedAge), biographicalReport
                 .getAgeAsOf(futureDate));
     }
}
