public class TestBid extends TestCase {

// &lt;snip&gt;

    // set up preconditions
    int enoughRunTime = 40;// milliseconds: if tests start to fail
                           // inexplicably, consider increasing this value
  public TestBid(String testName) {
    super(testName);
  }


public void testSTATE_BID_REQUESTSENT() {
    // set up preconditions
  bidMsgInQ.put(new SimpleMsg(prodnName, actorName, 0,
                                 MultipiMsg.PRODN_ALL_MET));
    standaloneBid = new Bid(actorName, prodnName, partName, rank,
      bidsMsgBoard, agentMsgBoard[rank], bidsReadyTable, bidMsgInQ,
      bidMsgOutQ);
    standaloneBid.setInitialState(MultipiState.BID_REQUESTSENT);
//  etc

// run Bid Thread
    Thread thr = new Thread(standaloneBid);
    thr.start();
    try { Thread.sleep(enoughRunTime);} catch (InterruptedException ie) {}
    standaloneBid.stopRunning();  // set  flag to force end of Thread execution
    try {thr.join();} catch (InterruptedException ie) {}

     // test postconditions
    assert(&quot;Bid thread should now be in state COMMITSENT&quot;,
    standaloneBid.state == MultipiState.BID_COMMITSENT);
   //  etc

